import React, { Component } from 'react'

export default class ToDoForm extends Component {
  constructor(props){
    super(props)
    this.state = {
      description: "",
      priority: ""
    }
  }

  handleDescriptionChange = event => {
    this.setState({
      description: event.target.value
    })
  }

  render(){
    return(
      <form>
        <input placeholder="description" value={this.state.description}></input>
        <input placeholder="priority" value={this.state.priority}></input>
        <button type="submit">Add ToDo</button>
      </form>
    )
  }
}
