import React, { Component } from 'react'
import ToDoForm from "./ToDoForm.js"
import ToDoTask from "./ToDoTask.js"
const todos = [
  { description: 'a', priority: 'low' },
  { description: 'b', priority: 'high' }
];
export default class ToDoList extends Component {
  constructor(){
    super()
    this.state = {
      todos: todos
    }
  }

  addToDo = newToDo => {
    this.setState(state =>{
      const list = state.todos.concat(newToDo)
      return {
        todos: list
      }
    })
  }

  render(){
    let tasks = this.state.todos.map(todo => <ToDoTask description={todo.description} priority={todo.priority}/>)
    return (
      <div>
        <ToDoForm addToDo={this.addToDo}/>
        {tasks}
      </div>
    )
  }
}
