import React, { Component } from 'react'

export default class ToDoForm extends Component {
  constructor(){
    super()
    this.state = {
      priority: "",
      description: "",
      completed: false
    }
  }

  handleChange = event => {
    this.setState({
      description: event.target.value
    })
  }
  render(){
    return(
      <form>
        <input type="text" placeholder= "description" onChange={this.handleChange}/>
      </form>
    )
  }
}
