import React, { Component } from 'react'

class InputLogger extends Component {
  constructor(){
    super()
    this.state = {
      inputData: {
        inputValue: "",
        length: 0
      }
    }
  }

  onChangeHandler = event => {
    this.setState({
      inputValue: event.target.value
    })
  }

  render(){
    return(
      <div>
        <input value={this.state.inputValue} placeholder="type here" onChange={this.onChangeHandler} type="text"/>
        <p>{this.state.inputValue}</p>
      </div>
    )
  }
}
export default InputLogger
