import React, { Component } from 'react'

class InputLogger extends Component {
  constructor(){
    super()
    this.state = {
      input: ""
    }
  }

  handleChange = event => {
    this.setState({
      input: event.target.value
    })
  }

  render(){
    return(
      <div>
        <input onChange={this.handleChange}/>
        <p>{this.state.input}</p>
      </div>
    )
  }
}
export default InputLogger
