import React, { Component } from 'react'

export default class ToDoForm extends Component {
  constructor(props){
    super(props)
    this.state = {
      description: "",
      priority: ""
    }
  }

  handleSubmit = event => {
    event.preventDefault()
    this.props.addToDo(this.state)
  }

  handleDescriptionChange = event => {
    this.setState({
      description: event.target.value
    })
  }

  handlePriorityChange = event => {
    this.setState({
      priority: event.target.value
    })

  }

  render(){
    return(
      <form onSubmit={this.handleSubmit}>
        <input onChange={this.handleDescriptionChange} placeholder="description" value={this.state.description}></input>
        <input onChange={this.handlePriorityChange} placeholder="priority" value={this.state.priority}></input>
        <button type="submit">Add ToDo</button>
      </form>
    )
  }
}
