import React, { Component } from "react"
import artwork from "./artwork.js"
import PaintingCard from "./PaintingCard.js"
export default class PaintingList extends Component {
  constructor(){
    super()
    this.state = {
      search: ""
    }
  }
  getSearchInput = event => {
    this.setState({
      search: event.target.value
    })
  }
  render(){
    return(
      <div>
        <header>
          <h1>Welcome to Artsy Fartsy!</h1>
          <p>Type here to find a specific painting</p>
          <input onChange={event => this.getSearchInput(event)}/>
        </header>
        <ul>
          {artwork.filter(painting => painting.title.toLowerCase()
            .includes(this.state.search)).map(painting => {
            return <PaintingCard
            title={painting.title}
            artistName={painting.artist.name}
            image={painting.image}
            votes={painting.votes}/>
          })}
        </ul>
      </div>
    )
  }
}
