import React, {Component} from "react"

export default class SearchBar extends Component {
  state = {
    value: ""
  }

  handleChange = input => {
    this.setState({
      value: input
    })
  }

  render(){
    return(
      <div>
        <input onChange={event => this.handleChange(event.target.value)} placeholder="search" />
        <button onClick={() => this.props.filterPaintings(this.state.value)}>Search!</button>
      </div>
    )
  }

}
