import artwork from "./artwork.js"

const defaultState = {
  paintings: artwork,
  searchInput: ""
}

export function rootReducer(state = defaultState, action){
  switch(action.type){
    case "UPVOTE":

      const votedPainting = state.paintings.find(painting => painting.id === action.id)
      console.log(votedPainting)
      // const votedPaintingIndex = state.paintings.findIndex(votedPainting)
      // console.log(votedPaintingIndex)
      return state
    default:
      return state
  }
}
