import artwork from "./artwork.js"

const defaultState = {
  paintings: artwork,
  searchInput: ""
}

export function rootReducer(state = defaultState, action){
  switch(action.type){
    case "UPVOTE":

      const votedPaintingIndex = state.paintings.findIndex(painting => painting.id === action.payload.id)
      const votedPainting = [...state.paintings][votedPaintingIndex]
      console.log(votedPainting)
      return state
    default:
      return state
  }
}
