import React, { Component } from "react"
import artwork from "./artwork.js"
import PaintingCard from "./PaintingCard.js"
export default class PaintingList extends Component {

  state = {
    paintings: artwork
  }

  upvote = artId => {
    let paintingIndex = this.state.paintings.findIndex(painting => painting.id === artId)
    let newState = [...paintings]
    newState[paintingIndex].votes += 1
    this.setState(newState)
  }

  render(){
    const renderAllPaintings = this.state.paintings.map(painting => {
      return <PaintingCard
      title={painting.title}
      artistName={painting.artist.name}
      image={painting.image}
      votes={painting.votes}/>
    })
    return(
      <ul>
        {renderAllPaintings}
      </ul>
    )
  }
}
