import React, {Component} from "react"
import {upvote} from "./actions"
export default class PaintingCard extends Component {



  render(){
    return (
      <li>
        <h3 >{this.props.title}</h3>
        <p>By {this.props.artistName}</p>
        <img src={this.props.image}/>
        <br/>
        <button onClick={() => this.props.upvote(this.props.id)}>Votes: {this.props.votes}</button>
      </li>
    )
  }
}

const mapDispatchToProps = dispatch => {
  return {
    dispatchUpvote: id => dispatch(upvote(id))
  }
}
