import React, {Component} from "react"

export default class SearchBar extends Component {


  render(){
    return(
      <div>
        <input onChange={event => this.props.onChangeInput(event.target.value)} value={this.props.value} placeholder="search" />
        <button onClick={() => this.props.filterPaintings(this.state.value)}>Search!</button>
      </div>
    )
  }
}
