import artwork from "./artwork.js"

const defaultState = {
  paintings: artwork,
  searchInput: ""
}

export function rootReducer(state = defaultState, action){
  switch(action.type){
    case "UPVOTE":
      console.log("action", action)
      const votedPaintingIndex = state.paintings.findIndex(painting => painting.id === action.id)
      const newPainting = [...state.paintings[votedPaintingIndex]]

      console.log(newPainting)
      return state
    default:
      return state
  }
}
