import artwork from "./artwork.js"

const defaultState = {
  paintings: artwork,
  searchInput: ""
}

export function rootReducer(state = defaultState, action){
  switch(action.type){
    case "UPVOTE":
      const newState = JSON.parse(JSON.stringify(state))
      const votedPaintingIndex = state.paintings.findIndex(painting => painting.id === action.payload.id)
      const votedPainting = JSON.parse(JSON.stringify(state.paintings))[votedPaintingIndex]
      votedPainting.votes += 1
      return state
    default:
      return state
  }
}
